mapper.select:
SELECT * FROM( SELECT a.*,rownum rid FROM( SELECT QLC_LOCALE_PUNISH.ID, PUNISH_ID, PUNISH_TIME, PUNISH_STYLE, PARTY_NAME, (select name from organizes_ciq t where t.org_code=QLC_LOCALE_PUNISH.PORT_DEPT_CODE) as PORT_DEPT_CODE, (select name from QLC_CODE_LIBRARY t where t.code=QLC_LOCALE_PUNISH.PORT_ORG_CODE and t.TYPE='QLC_ORGANIZES') as PORT_ORG_CODE, qlp.ILLEGAL_ACT as ILLEGAL_CONTENT FROM QLC_LOCALE_PUNISH LEFT JOIN (SELECT NAME,CODE FROM QLC_CODE_LIBRARY WHERE QLC_CODE_LIBRARY.TYPE='QLC_ORGANIZES' ) FIRST ON QLC_LOCALE_PUNISH.PORT_ORG_CODE=FIRST.CODE LEFT JOIN (SELECT NAME,CODE FROM QLC_CODE_LIBRARY WHERE QLC_CODE_LIBRARY.TYPE='QLC_DEPTMENTS' ) SECOND ON QLC_LOCALE_PUNISH.PORT_DEPT_CODE=SECOND.CODE LEFT JOIN QLC_LEGAL_PROVISIONS qlp ON qlp.code=QLC_LOCALE_PUNISH.ILLEGAL_CONTENT and qlp.punish_type='1' WHERE 1=1
AND QLC_LOCALE_PUNISH.PUNISH_TIME >= to_date(#{start_date}, 'yyyy-mm-dd')
AND CQLC_LOCALE_PUNISH.PUNISH_TIME &lt;= to_date(#{end_date},'yyyy-mm-dd')
AND QLC_LOCALE_PUNISH.PARTY_NAME = #{party_name}
AND QLC_LOCALE_PUNISH.ILLEGAL_CONTENT = #{illegal_content}
AND QLC_LOCALE_PUNISH.PUNISH_STYLE = #{punish_style}
AND QLC_LOCALE_PUNISH.PORT_ORG_CODE = #{port_org_code}
AND QLC_LOCALE_PUNISH.PORT_DEPT_CODE = #{port_dept_code}
ORDER BY QLC_LOCALE_PUNISH.PUNISH_TIME DESC ) a WHERE rownum <#{lastRcd} ) WHERE rid>=#{firstRcd}

	SELECT 
	            count(PUNISH_ID) as TOTALPAGES 
	FROM 
	            QLC_LOCALE_PUNISH 
	WHERE 
	            1=1
	            
	               
    AND               QLC_LOCALE_PUNISH.PUNISH_TIME >= to_date(#{start_date}, 'yyyy-mm-dd')
                  
				
				
				  
    AND               CQLC_LOCALE_PUNISH.PUNISH_TIME &lt;= to_date(#{end_date},'yyyy-mm-dd')
                  
				
				
    AND          QLC_LOCALE_PUNISH.PARTY_NAME = #{party_name}
				
				
    AND          QLC_LOCALE_PUNISH.ILLEGAL_CONTENT = #{illegal_content}
				
				
    AND          QLC_LOCALE_PUNISH.PUNISH_STYLE = #{punish_style}
	            
	            
    AND          QLC_LOCALE_PUNISH.PORT_ORG_CODE = #{port_org_code}
	            
	            
    AND          QLC_LOCALE_PUNISH.PORT_DEPT_CODE = #{port_dept_code}

SELECT ID,PUNISH_ID,PUNISH_TIME,PARTY_NAME,IDENTIFI_STYLE,IDENTIFI_NUMBER,ADDRESS,LEGAL_REPRESENT,LEGAL_REPRESENT_PHONE,ILLEGAL_OBJECT,ILLEGAL_ADDRESS,ILLEGAL_TIME,PARTY_REPRESENT, PARTY_REPRESENT_PHONE,SURVEY_CONTENT,PARTY_ASSIGN,ILLEGAL_CONTENT,LEGISLATIVE_AUTHORITY,PUNISH_STYLE,WARNNING_CONTENT,PUNISH_MONEY,FORFEIT_PAY_STYLE,FORFEIT_STATUS,PAY_TIME, BANK_CARD,RECONSIDER,LAW_EXECUTOR,RESULT_STATUS,cur_psn_sign, (select name from QLC_CODE_LIBRARY t where t.code=QLC_LOCALE_PUNISH.PORT_ORG_CODE and t.TYPE='QLC_ORGANIZES') as PORT_ORG_CODE, (select name from organizes_ciq t where t.org_code=QLC_LOCALE_PUNISH.PORT_DEPT_CODE) as PORT_DEPT_CODE, (select illegal_act from QLC_LEGAL_PROVISIONS t where t.code=QLC_LOCALE_PUNISH.ILLEGAL_CONTENT and t.punish_type='1') as ILLEGAL_ACT FROM QLC_LOCALE_PUNISH LEFT JOIN (SELECT NAME,CODE FROM QLC_CODE_LIBRARY WHERE QLC_CODE_LIBRARY.TYPE='QLC_ORGANIZES' ) FIRST ON QLC_LOCALE_PUNISH.PORT_ORG_CODE=FIRST.CODE LEFT JOIN (SELECT NAME,CODE FROM QLC_CODE_LIBRARY WHERE QLC_CODE_LIBRARY.TYPE='QLC_DEPTMENTS' ) SECOND ON QLC_LOCALE_PUNISH.PORT_DEPT_CODE=SECOND.CODE WHERE PUNISH_ID=#{punish_id}
AND QLC_LOCALE_PUNISH.PORT_ORG_CODE = #{port_org_code}
AND QLC_LOCALE_PUNISH.PORT_DEPT_CODE = #{port_dept_code}

SELECT QLC_VIDEO_FILE_EVENT.ID, QLC_VIDEO_FILE_EVENT.FILE_TYPE, QLC_VIDEO_FILE_EVENT.FILE_NAME, QLC_VIDEO_FILE_EVENT.PORT_DEPT_CODE, QLC_VIDEO_FILE_EVENT.PORT_ORG_CODE, QLC_VIDEO_FILE_EVENT.CREATE_USER, QLC_VIDEO_FILE_EVENT.CREATE_DATE, QLC_C_VIDEO_PROC_TYPE.PROC_NAME, (select name from USERS_CIQ t where t.id=QLC_VIDEO_FILE_EVENT.CREATE_USER) as CREATE_USER_NAME, to_char(QLC_VIDEO_FILE_EVENT.CREATE_DATE,'yyyy-mm-dd hh:mi:ss') as create_date_str FROM QLC_VIDEO_FILE_EVENT LEFT JOIN QLC_C_VIDEO_PROC_TYPE ON QLC_C_VIDEO_PROC_TYPE.PROC_TYPE= QLC_VIDEO_FILE_EVENT.PROC_TYPE WHERE QLC_VIDEO_FILE_EVENT.PROC_MAIN_ID=#{punish_id}
AND QLC_VIDEO_FILE_EVENT.PORT_ORG_CODE = #{port_org_code}
AND QLC_VIDEO_FILE_EVENT.PORT_DEPT_CODE = #{port_dept_code}
AND EXISTS ( SELECT PROC_TYPE FROM QLC_C_VIDEO_PROC_TYPE WHERE PROJ_CODE=#{proj_code} AND PROC_TYPE=QLC_VIDEO_FILE_EVENT.PROC_TYPE ) ORDER BY QLC_VIDEO_FILE_EVENT.FILE_TYPE, QLC_VIDEO_FILE_EVENT.CREATE_DATE DESC

SELECT QLC_CHECK_DOCS_RCD.*, QLC_C_CHECK_DOCS_RCD_TYPE_CODE.NAME FROM QLC_CHECK_DOCS_RCD LEFT JOIN QLC_C_CHECK_DOCS_RCD_TYPE_CODE ON QLC_C_CHECK_DOCS_RCD_TYPE_CODE.CODE= QLC_CHECK_DOCS_RCD.DOC_TYPE LEFT JOIN QLC_LOCALE_PUNISH ON QLC_C_CHECK_DOCS_RCD_TYPE_CODE.CODE= QLC_LOCALE_PUNISH.PUNISH_ID WHERE QLC_CHECK_DOCS_RCD.PROC_MAIN_ID=#{punish_id}
AND QLC_LOCALE_PUNISH.PORT_ORG_CODE = #{port_org_code}
AND QLC_LOCALE_PUNISH.PORT_DEPT_CODE = #{port_dept_code}
AND EXISTS ( SELECT CODE FROM QLC_C_CHECK_DOCS_RCD_TYPE_CODE WHERE PROJ_CODE=#{proj_code} AND CODE=QLC_CHECK_DOCS_RCD.DOC_TYPE ) ORDER BY QLC_CHECK_DOCS_RCD.DEC_DATE DESC

