mapper.insert:
begin
INSERT INTO dca_page_config( cfg_id, cfg_type, cfg_code, cfg_name, cfg_value1, cfg_value2, cfg_value3, cfg_value4, cfg_value5, cfg_value6, del_flag, create_person, create_date, update_person, update_date, remarks ) VALUES ( #{item.cfgId}, #{item.cfgType}, #{item.cfgCode}, #{item.cfgName}, #{item.cfgValue1}, #{item.cfgValue2}, #{item.cfgValue3}, #{item.cfgValue4}, #{item.cfgValue5}, #{item.cfgValue6}, #{item.delFlag}, #{item.createPerson}, SYSDATE, #{item.updatePerson}, SYSDATE, #{item.remarks} )
;end;

mapper.update:
UPDATE dca_page_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
