mapper.select:
SELECT

FROM DCA_WORKFLOW_BASIC_IDX a

WHERE a.IDX_ID = #{id}

SELECT

FROM DCA_WORKFLOW_BASIC_IDX a


	a.DEL_FLAG = #{DEL_FLAG_NORMAL}

		AND a.IDX_NAME LIKE
		'%'||#{idxName}||'%'
		'%'+#{idxName}+'%'
		CONCAT('%',#{idxName},'%')


		AND a.IDX_DATA_TYPE = #{idxDataType}




		ORDER BY ${page.orderBy}


		ORDER BY a.UPDATE_DATE DESC

SELECT

FROM DCA_WORKFLOW_BASIC_IDX a


	a.DEL_FLAG = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.UPDATE_DATE DESC

mapper.update:
UPDATE DCA_WORKFLOW_BASIC_IDX SET
DEL_FLAG =
#{DEL_FLAG_DELETE}
WHERE IDX_ID = #{id}
