mapper.select:
SELECT 

FROM sys_dict_custom a

WHERE a.id = #{id}

SELECT 

FROM sys_dict_custom a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_dict_custom a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_dict_custom SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM SYS_DICT_CUSTOM a 
WHERE DEL_FLAG = '0'

	AND LABEL = #{label}		 


	AND TYPE = #{type}
