sql.where:
del_flag = '0'
and id = #{id}
and shop_type = #{shopType}
and name = #{name}
and mobile = #{mobile}
and pwfirst = #{pwfirst}
and pwsecond = #{pwsecond}
and phone = #{phone}
and fax = #{fax}
and password = #{password}
and papers_type = #{papersType}
and papers_num = #{papersNum}
and is_first = #{isFirst}
and address = #{address}
and email = #{email}
and status = #{status}
and descript = #{descript}
and right_code = #{rightCode}
and level = #{level}
and login_time = #{loginTime}
and login_count = #{loginCount}
and error_count = #{errorCount}
and check_status = #{checkStatus}
and refuse_desc = #{refuseDesc}
and create_oper = #{createOper}
and modify_oper = #{modifyOper}
and create_time = #{createTime}
and modify_time = #{modifyTime}
and edit_pw_time = #{editPwTime}
and is_sub = #{isSub}
and parent_id = #{parentId}
and vendor_id = #{vendorId}
and simple_name = #{simpleName}
and code = #{code}

del_flag = '0'
and (check_status = '0' or check_status = '2')
and vendor_id like concat(concat('%',#{vendorId}),'%')
and simple_name like concat(concat('%',#{simpleName}),'%')
and shop_type = #{shopType}
and is_sub = #{isSub}
and status = #{status}
and code like concat(concat('%',#{code}),'%')
and name like concat(concat('%',#{name}),'%')
and id in
    
        #{item}

