sql.where:
and order_id LIKE concat(#{orderId},'%')
and ordermain_id LIKE concat(#{ordermainId},'%')
and cur_status_id ='0308'
and del_flag = 0
and goods_type = '00'
and create_time = #{createTime}
and modify_time = #{modifyTime}
and vendor_id = #{vendorId}
and ordertype_id = #{ordertypeId}
and ordertype_id in('YG','FQ')
and source_id = #{sourceId}
and  create_time >= date(#{startTime}) 
and  create_time <= date(#{endTime})

